			new Swiper(slider, {
				observer: true,
				observeParents: true,
				slidesPerView: 1,
				spaceBetween: 0,
				speed: 800,
				loop: true,
				navigation: {
					nextEl: btnNext,
					prevEl: btnPrev,
				},
				...setting

			});
