const formImage = document.getElementById('formImage');
//Получаем див для превью в переменную
const formPreview = document.getElementById('formPreview');

//Слушаем изменения в инпуте file
if(formImage)
formImage.addEventListener('change', () => {
	uploadFile(formImage.files[0]);
});

function uploadFile(file) {
	// провераяем тип файла
	if (!['"application/msword', 'application/vnd.ms-excel', 'application/vnd.ms-powerpoint',
	'text/plain', 'application/pdf', 'application/msword'].includes(file.type)) {
		alert('Разрешены только документы.');
		formImage.value = '';
		return;
	}
	// проверим размер файла (<2 Мб)
	if (file.size > 2 * 1024 * 1024) {
		alert('Файл должен быть менее 2 МБ.');
		return;
	}

	var reader = new FileReader();
	reader.onload = function (e) {
		formPreview.innerHTML = 'Файл загружен!';
	};
	reader.onerror = function (e) {
		alert('Ошибка');
	};
	reader.readAsDataURL(file);
}
